export default {
	Init:()=>{
		storeValue('key_UpdatePlayer_Payload', "");
		storeValue('key_AddPlayer_Payload', "");
		storeValue('key_AddPlayer_AddAPIFail', "");
		storeValue('key_UpdatePlayer_AddAPIFail', "");
	},
	GetPlayer:() => {
		JSObject1.Init();
		GetInstallation.run(
			// success
			() => {GetPlayersInstallation.run(
				//success
				() => {},
				//fail
				() => {showAlert(JSON.stringify(GetPlayersInstallation.data.message));}
			);},
			//fail
			() => {showAlert(JSON.stringify(GetInstallation.data.message));}		
		);

	},
	DeleteInstallation:()=>{
		DeletePlayersInstallation.run(
			//success
			() => {GetPlayersInstallation.run();closeModal('ModalDelete');},
			//fail
			() => {showAlert("Something went wrong - Can't delete player");}
		)
	},	
	UpdatePlayer:()=>{	
		if(Input_InstallationID.text==""){
			showAlert("Installation ID is required");
		}
		else{			
			let payload_update = [];
			let buildPayload = {};
			buildPayload = {
				"id": ListPlayers.selectedRow.Id,
				"role": EditRole.selectedOptionValue,
				"playerId": ListPlayers.selectedRow.PlayerId,
				"since": (EditSince.text==""?null:EditSince.text),
				"until": (EditUntil.text==""?null:EditUntil.text)
			}
			if(EditRole.selectedOptionValue=="owner"){
				buildPayload.share = EditShare.text;
			}
			else{
				buildPayload.share = null;
			}
			payload_update.push(buildPayload);
			storeValue("key_UpdatePlayer_Payload",payload_update);

			UpdatePlayersInstallation.run(
				//success	
				() => {storeValue("key_UpdatePlayer_AddAPIFail","");JSObject1.GetPlayer();closeModal('ModalEdit')},
				//fail
				() => {showAlert("Something went wrong - Player not updated"); storeValue("key_UpdatePlayer_AddAPIFail", UpdatePlayersInstallation.data.message)}
			)			
		}		
	},
	AddPlayer:()=>{		
		if(Input_InstallationID.text==""){
			showAlert("Installation ID is required");
		}
		else{
			let payload_update = [];
			let buildPayload = {};
			buildPayload = {
				"role": AddRole.selectedOptionValue,
				"playerId": AddPlayerId.text,
				"since": (AddSince.text==""?null:AddSince.text),
				"until": (AddUntil.text==""?null:AddUntil.text)
			}
			if(AddRole.selectedOptionValue=="owner"){
				buildPayload.share = AddShare.text;
			}
			payload_update.push(buildPayload);
			storeValue("key_AddPlayer_Payload",payload_update)

			AddPlayersInstallation.run(
				//success	
				() => {storeValue("key_AddPlayer_AddAPIFail","");JSObject1.GetPlayer();closeModal('ModalAdd')},
				//fail
				() => {showAlert("Something went wrong - New Player not inserted"); storeValue("key_AddPlayer_AddAPIFail", AddPlayersInstallation.data.message)}
			)			
		}
	}
}
