export default {	
	Init:() =>{		
		if(Tabs1.selectedTab=="Get Constraints"){
			storeValue("key_GetVPC_Response","") ;
			storeValue("key_GetVPC_APIFail", "");
			storeValue('key_SelectedVessel', "");
		}
		else if(Tabs1.selectedTab=="Create Constraints"){

		}
	},
	GetVPCs:()=>{		
		storeValue('key_getVPC_VesselIds', "69312&amp;116967");
		switch(Platform.selectedOptionLabel){
			case "Liquids":
				Liquids_GetVPC.run(
					//success
					()=>{
							storeValue("key_GetVPC_Response",Liquids_GetVPC.data) ;		
							if (Liquids_GetVPC.data == null || Liquids_GetVPC.data=="" ){
								storeValue("key_GetVPC_APIFail", "No Vessel Product Constraint Found");
							}
							else{
								storeValue("key_GetVPC_APIFail", "");
							}
					},
					//fail
					()=>{			
							if (Liquids_GetVPC.data == null || Liquids_GetVPC.data=="" ){
								showAlert("Something went wrong - Can't connect to the API - PLEASE LOG IN AGAIN")
							}
							else{
								storeValue("key_GetVPC_Response", ''); storeValue("key_GetLPC_APIFail", Liquids_GetVPC.data.errors); showAlert(JSON.stringify(Liquids_GetVPC.data.errors))}
							}
				);
				break;
			case "LPG":
				LPG_GetVPC.run(
					//success
					()=>{
							storeValue("key_GetVPC_Response",LPG_GetVPC.data) ;
							if (LPG_GetVPC.data == null || LPG_GetVPC.data=="" ){
								storeValue("key_GetVPC_APIFail", "No Vessel Product Constraint Found");
							}
							else{
								storeValue("key_GetVPC_APIFail", "");
							}
							},
					//fail
					()=>{			
							if (LPG_GetVPC.data == null || LPG_GetVPC.data=="" ){
								showAlert("Something went wrong - Can't connect to the API - PLEASE LOG IN AGAIN")
							}
							else{
								storeValue("key_GetVPC_Response", ''); storeValue("key_GetLPC_APIFail", LPG_GetVPC.data.errors); showAlert(JSON.stringify(LPG_GetVPC.data.errors))}
							}
				);
				break;			
			case "LNG":
				LNG_GetVPC.run(
					//success
					()=>{
							storeValue("key_GetVPC_Response",LNG_GetVPC.data) ;
							if (LNG_GetVPC.data == null || LNG_GetVPC.data=="" ){
								storeValue("key_GetVPC_APIFail", "No Vessel Product Constraint Found");
							}
							else{
								storeValue("key_GetVPC_APIFail", "");
							}
							},
					//fail
					()=>{			
							if (LNG_GetVPC.data == null || LNG_GetVPC.data=="" ){
								showAlert("Something went wrong - Can't connect to the API - PLEASE LOG IN AGAIN")
							}
							else{
								storeValue("key_GetVPC_Response", ''); storeValue("key_GetLPC_APIFail", LNG_GetVPC.data.errors); showAlert(JSON.stringify(LNG_GetVPC.data.errors))}
							}
				);
				break;				
			case "Dry":
				Dry_GetVPC.run(
					//success
					()=>{
							storeValue("key_GetVPC_Response",Dry_GetVPC.data) ;
							if (Dry_GetVPC.data == null || Dry_GetVPC.data=="" ){
								storeValue("key_GetVPC_APIFail", "No Vessel Product Constraint Found");
							}
							else{
								storeValue("key_GetVPC_APIFail", "");
							}
							},
					//fail
					()=>{			
							if (Dry_GetVPC.data == null || Dry_GetVPC.data=="" ){
								showAlert("Something went wrong - Can't connect to the API - PLEASE LOG IN AGAIN")
							}
							else{
								storeValue("key_GetVPC_Response", ''); storeValue("key_GetLPC_APIFail", Dry_GetVPC.data.errors); showAlert(JSON.stringify(Dry_GetVPC.data.errors))}
							}
				);
				break;					
		}
	},
	SelectVessel:()=>{
		storeValue('key_SelectedVessel', SelectVessel.selectedRow.id);
		closeModal('Modal_VesselSearch');
		JSObject1.GetVPCs(); 
	},
	SelectProduct:()=>{
		let array_products = [];
		array_products = appsmith.store.key_VPCEdit_ProductList;
		array_products.push(SelectProduct.selectedRow.id);
		storeValue("key_VPCEdit_ProductList", array_products);
		closeModal('Modal_ProductSearch');
		showModal('ModalEdit');
	} ,
	UpdateVPC:()=>{
		let buildPayload={};
		buildPayload = {
			"providerId": EditProvider.text,
			"start": (EditStart.text==""?null:EditStart.text+"T00:00:00"),
			"type": EditType.text,
			"vesselId": EditVessel.text,
			"productIds": appsmith.store.key_VPCEdit_ProductList,
			"end": (EditEnd.text==""?null:EditEnd.text+"T00:00:00")
		};

		storeValue("key_VPCEdit_payload", buildPayload);

		switch(Platform.selectedOptionLabel){
			case "Liquids":
				Liquids_UpdateVPC.run(
					//success
					()=>{
							closeModal('ModalEdit');
							JSObject1.GetVPCs();
					},
					//fail
					()=>{	
								storeValue("key_UpdateVPC_APIFail", Liquids_UpdateVPC.data.errors);
					}							
				);
				break;
			case "LPG":
				LPG_UpdateVPC.run(
					//success
					()=>{
							closeModal('ModalEdit');
							JSObject1.GetVPCs();
							},
					//fail
					()=>{			
								storeValue("key_UpdateVPC_APIFail", LPG_UpdateVPC.data.errors);
							}
				);
				break;			
			case "LNG":
				LNG_UpdateVPC.run(
					//success
					()=>{
							closeModal('ModalEdit');
							JSObject1.GetVPCs();
							},
					//fail
					()=>{		
								storeValue("key_UpdateVPC_APIFail", LNG_UpdateVPC.data.errors);
							}
				);
				break;				
			case "Dry":
				Dry_UpdateVPC.run(
					//success
					()=>{
							closeModal('ModalEdit');
							JSObject1.GetVPCs();
							},
					//fail
					()=>{			
								storeValue("key_UpdateVPC_APIFail", Dry_UpdateVPC.data.errors);
							}
				);
				break;					
		}

	}
}
