actionConfiguration.body:
()=>{
		// build payload		
		let array_payload=[];
		array_payload.push({
			"alias": Add_Alias.text,
			"productId":Add_ProductId.text,
			"providerId": ((Add_ProviderId.text==null||Add_ProviderId.text=="")?null:Add_ProviderId.text),
			"sourceId": ((Add_SourceId.text==null||Add_SourceId.text=="")?null:Add_SourceId.text)
		})						
		storeValue("Key_Alias_UserPayload", array_payload);

		AddProductAlias.run(
			// success
			() => {
				if (AddProductAlias.data[0].statuses[0].status_code>=400) {
					showAlert(JSON.stringify(AddProductAlias.data[0].statuses[0].message));
				}
				else{
					JSObject1.GetAliases(); 
					closeModal('Modal_Add');
				}
				},				
			//fail
			() => {showAlert(JSON.stringify(AddProductAlias.data.message)); })
	}

