actionConfiguration.body:
()=>{		
		//reinit key_EditLPC_payload
		storeValue('key_EditLPC_payload', "");
		// Build body for Gsheet content
		let putBody = [];
		let curConstraint="";
		let curConstraintArray=[];
		let i=1;
		let j=1;
		let curArray=[];
		let ConstraintIdArray = Constraints.selectedRow.ConstraintsForPopup;
		// split the description of constraints displayed to user. Pattern: ([..,..,..],[..,..,..],[..,..,..])
		let contraintsArray = Constraints.selectedRow.Constraints.split("],")
		for (i=0; i<contraintsArray.length; i++){
			curArray=[];
			curConstraint = contraintsArray[i].replace("[", "");
			curConstraint = curConstraint.replace("]", "");
			curConstraintArray = curConstraint.split(",");
			if (i==0){
				curArray.push(
					Constraints.selectedRow.Type,
					Constraints.selectedRow.OriginRessourceId,
					Constraints.selectedRow.OriginRessourceType,
					Constraints.selectedRow.DestinationRessourceId,
					Constraints.selectedRow.DestinationRessourceType,
					Constraints.selectedRow.Start,
					Constraints.selectedRow.End,
					Constraints.selectedRow.minDwt,
					Constraints.selectedRow.maxDwt,
					Constraints.selectedRow.minCapacity,
					Constraints.selectedRow.maxCapacity
				);
			}
			else{
				curArray.push("","","","","","","","","","","");
			}
			curArray.push(ConstraintIdArray[i].ConstraintId);
			for (j=0; j<curConstraintArray.length; j++){
				if(j==0){
					//product Name
					curArray.push(curConstraintArray[j].split(" - ")[1]);
				}
				else{
					//other prm
					curArray.push(curConstraintArray[j].split(":")[1]);
				}
			}
			putBody.push(curArray);
		}
		storeValue('key_LPCEdit_Body', putBody);		
		storeValue('key_LPC_EditGroupId', Constraints.selectedRow.GroupId);

		CreateGSheet_EditLPC.run(
			//success
			() =>{
				PatchGSheetContent_EditLPC.run(
					//success
					() => {navigateTo(CreateGSheet_EditLPC.data.webViewLink,{},'NEW_WINDOW');showModal('EditConfirm');},
					() => {showAlert('Something wrong happened - cannot open Gsheet for edit')}				
				);				
			},
			//fail
			() =>{showAlert('Something wrong happened - cannot open Gsheet for edit')}		
		)	

	}

