actionConfiguration.body:
() => {	
		Liquids_Login.clear();
		if(UserName.text=="" || UserPwd.text=="") {
			showAlert("User Name and Password required");
		}
		else{			
			storeValue('key_UserName', UserName.text);
			storeValue('key_UserPwd', UserPwd.text);

			Liquids_Login.run(
				() => {
					storeValue('key_Token', Liquids_Login.data.token);
					storeValue('key_UserId', Liquids_Login.data.user.id);
					storeValue('key_LoginFail', Liquids_Login.data.message);
					storeValue('key_Referer', 'https://terminal.kpler.com');			
					showAlert('Login successful');
				},
				() => {showAlert("Can't login to Liquids API")}
			)
		}
	}

