actionConfiguration.body:
()=>{	
		if(Input_VesselIMO.text==""){
			showAlert("Vessel ID is required");
		}
		else{			
			let payload_update = [];
			let buildPayload = {};
			buildPayload = {
				"id": ListPlayers.selectedRow.Id,
				"share": (EditShare.text==""?null:EditShare.text),
				"since": (EditSince.text==""?null:EditSince.text),
				"until": (EditUntil.text==""?null:EditUntil.text)
			}
			payload_update.push(buildPayload);
			storeValue("key_UpdatePlayer_Payload",payload_update);

			UpdatePlayersVessels.run(
				//success	
				() => {storeValue("key_UpdatePlayer_AddAPIFail","");JSObject1.GetPlayer();closeModal('ModalEdit')},
				//fail
				() => {showAlert("Something went wrong - Player not updated"); storeValue("key_UpdatePlayer_AddAPIFail", UpdatePlayersVessels.data.message)}
			)			
		}		
	}

